import classes from "./HeaderCartButton.module.css"
import CardIcon from "../../Card/CartIcon/CardIcon";
import {useContext, useEffect, useState} from "react";
import CardContext from "../../../store/cart-context";
const HeaderCartButton = (props) => {
    const {items } = useContext(CardContext);
    const [isBtnHighlighted,setIsBtnHighlighted] =useState(false)
    const numberOfCardItem = items.reduce((currentNumber, item)=>{
        return currentNumber + item.amount
    },0)

    const btnClasses = `${classes.button} ${setIsBtnHighlighted?classes.bump:''}`

    useEffect(()=>{
        if(items.length===0) {
            setIsBtnHighlighted(true)

        }
    },[items])

    return <button className={classes.button} onClick={props.onClick}>
        <span className={classes.icon}><CardIcon/></span>
        <span>Your Card</span>
        <span className={classes.badge}>{numberOfCardItem}</span>
    </button>
}

export default HeaderCartButton;
