import classes from "./MealItemForm.module.css"
import Input from "../../../UI/Input/Input";
import {useContext, useRef, useState} from "react";
import CardContext from "../../../../store/cart-context";

const MealItemForm = (props) => {
    const {items, removeItem, addItem} = useContext(CardContext)

    const [isAmountValid, setIsAmountValid] = useState(true);
    const inputValue =items.find((item) => item.id===props.id)?.amount || 0;


    const addHandler = e => {
        props.onAddToCard(inputValue + 1)
       /* if (inputValue.trim().length === 0 || inputValue < 1) {
            setIsAmountValid(false);
            return;
        }*/
    }

    const removeHandler = e => {
        props.onRemoveToCard()

    }

    const changeInputHandler = (e) => {
        props.onAddToCard(e.currentTarget.value)

    }


    return <form className={classes.form}>
        <button onClick={removeHandler} type="button" >âˆ’</button>

        <Input
            label="Amount"
            input={{
                id: 'amount_' + props.id,
                type: "number",
                min: "0",
                step: "1",
                value: inputValue
            }}
            onChange={changeInputHandler}
        />
        <button onClick={addHandler} type="button">+</button>
        {!isAmountValid && <p>Please entered valid value</p>}
    </form>
}

export default MealItemForm;
